import React from 'react';
import styled,{css} from 'styled-components';
import { MdDone, MdDelete } from 'react-icons/md';

const Todoitembox = styled.div`
  display: flex;
  align-items : center;
  padding-top: 10px;
  padding-bottom: 10px;
  /* background-color: beige;
  border-bottom: 1px solid #e9ecef; */
  &:hover {
    ${Remove} {
      display: initial;
    }
  }
`
const Circle = styled.div`
  width: 32px;
  height: 32px;
  border-radius: 16px;
  border: 1px solid #ced4da;
  font-size: 24px;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-right: 20px;
  cursor: pointer;
  ${props =>
    props.done &&
    css`
      border: 1px solid #38d9a9;
      color: #38d9a9;
    `}
`
const Text = styled.div`
  font-size: 20px;
  color: #495057;
`
const Remove = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  color: #dee2e6;
  font-size: 24px;
  cursor: pointer;
  &:hover {
    color: #ff6b6b;
  }
  display: none;
`;
function Todoitem({done, text}) {
  return(
    <Todoitembox>
      <Circle done= {done}>{done &&<MdDone />}</Circle>
      <Text done= {done}>{text}</Text>
      <Remove><MdDelete /></Remove>
    </Todoitembox>
  )
}

export default Todoitem;
